var payload = oa_data.getMetadata().get("origPayload");
if (payload != "apple") {
	oa_data = "";
}
else {
	oa_data = "This was an apple."
}


var payload = oa_data.getMetadata().get("origPayload");
if (payload != "pear") {
	oa_data = "";
}
else {
	oa_data = "This was a pear."
}


var payload = oa_data.getMetadata().get("origPayload");
if (payload != "apple" && payload != "pear") {
	oa_data = "This was an odd fruit!";
}
else {
	oa_data = ""
}
